<?php/** * Created by PhpStorm. * User: donggege * Date: 2019-05-18 * Time: 15:57 */namespace App\DataStructure;class Stack implements StackInterface{    protected $items = [];    public function push($value)    {        return array_push($this->items, $value);    }    public function pop()    {        return array_pop($this->items);    }    public function peek()    {        return $this->items[$this->size() - 1];    }    public function isEmpty()    {        return empty($this->items);    }    public function clear()    {        return $this->items = [];    }    public function size()    {        return count($this->items);    }    /**     * @return array     */    public function getItems(): array    {        return $this->items;    }}interface StackInterface{    /**     * 入栈     * @param $value     * @return mixed     */    public function push($value);    /**     * 出栈     * @return mixed     */    public function pop();    /**     * 返回栈顶的元素，不对栈做任何修改     * @return mixed     */    public function peek();    /**     * 如果栈里没有任何元素就返回true，否则返回false     * @param $value     * @return mixed     */    public function isEmpty();    /**     * 清空栈     * @return mixed     */    public function clear();    /**     * 返回栈的大小     * @return mixed     */    public function size();}